import { Component } from '@angular/core';
import { FormBuilder, Validators } from '@angular/forms';
import { Router } from '@angular/router';
import { AuthService } from 'src/app/services/auth.service';

@Component({
  selector: 'app-sign-in',
  templateUrl: './signin.component.html'
})
export class SignInComponent {
  public inLoading: boolean = false;
  public isError: boolean = false;
  public message: string = ""

  constructor(
    private fb: FormBuilder,
    private service: AuthService,
    private router: Router
    ) {
  }

  login = this.fb.group({
    acUser: ['', [Validators.required, Validators.minLength(5), Validators.pattern("[a-z0-9._%+-]+@[a-z0-9.-]+\.[a-z]{2,3}$")]],
    acPasword: ['', [Validators.required, Validators.minLength(5)]]
  });

  getUser(){ return this.login.get('acUser')?.invalid && this.login.get('acUser')?.touched;}

  getPassword(){ return this.login.get('acPasword')?.invalid && this.login.get('acPasword')?.touched;}

  evtValidLogin(){
    if(this.login.invalid){
      return Object.values(this.login.controls).forEach(control => {
        control.markAllAsTouched();
      });
    }
    this.inLoading = true;
    this.service.auth(this.login.value).subscribe({
      next: (response) => {
        this.inLoading = false;
        this.message = response.message;
        if(response.ok){
          localStorage.setItem("strToken", response.data.strToken);
          localStorage.setItem("strCode", response.data.strCode);
          localStorage.setItem("fullName", response.data.fullName);
          this.router.navigate(['/home']);
        }else{
          this.mdlShow = true;
          this.mdlTitle = "Error " + response.title;
          this.mdlMessage = response.message;
          this.textBtnLogin = "Iniciar Sesión"
          this.isLoading = false
        }
      },
      error: (error) => {
        this.inLoading = false;
        this.message = "Ocurrió un error por favor inténtalo más tarde.";
      }
    })
  }
}
